import Layout from "../components/Layout";
import HobbyHub from "../Api/HobbyHub";


class Register extends React.Component {
  constructor(props) {
    super(props);
    this.state = {email: '', password: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  Change(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <Layout>
        <div>
        <form action="/user/signup" method="POST" className="form-signin">
        <div className="form-label-group">
          <input value={this.state.email} type="email" id="inputEmail" name="email" className="form-control" placeholder="Email" required autofocus />
        </div>
          <hr />
        <div className="form-label-group">
          <input value={this.state.password} type="password" id="inputPassword" name="password" className="form-control" placeholder="Password" required />
        </div>

        <div className="custom-control custom-checkbox mb-3">
          <input type="checkbox" className="custom-control-input" id="customCheck1" />
        </div>
        <button className="btn btn-lg btn-primary btn-block text-uppercase" type="submit">Sign Up!</button>

  </form>
        </div>
      </Layout>
    )
  }

}
 export default Register;
