import List from '../components/TodoList';
import HobbyHub from '../Api/HobbyHub';

class TodoForm extends React.Component {
  state={
    term: '',
    items:[]
  }

  onChange = (event) => {
    this.setState({ term: event.target.value });
	}

	updateTodo = (data) => {
		this.setState({
      items: [...data]
    });
  }

  setItem=(token)=>{
    localStorage.setItem('token', {value:token})
    localStorage.getItem('token')
  }

  onSubmit =  (event) => {
    event.preventDefault();
    HobbyHub.post('/hobbies',{term:this.state.term})
    .then(res=>{
      console.log("Hobby Route",{res})
      con
      setItem(res.data.token)
    }).catch(err=>{
      console.log({err});
    })
  if (this.state.term) {
		this.setState({
      term: '', 
      items: [...this.state.items, this.state.term]
    });
	}
  }

  render() {
    return (
      <div className="">
      <div className="card-title"><h2>Todo List App</h2></div>
        <form className="" onSubmit={this.onSubmit}>
          <input value={this.state.term} onChange={this.onChange} type="text" placeholder="Add a todo list..." />
          <button type="button" className="btn btn-primary">Add</button>
        </form>
        <List items={this.state.items} updateTodo={this.updateTodo} /> 
      </div>
    );
  }
}

export default TodoForm;
